import React from "react";
import {useState, useEffect} from "react";
import getCommonOptions from "./getCommonOptions";
function Checkout(){
    let [branches, setBranches]=useState([]);
    let [show, setShow]=useState([]);
    
    
    const fetchData=()=>{
           
        fetch('http://127.0.0.1:8000/franchise/', getCommonOptions()).then(response=>response.json())
        .then((data)=>{
            console.log(data);
            setBranches(data);
        }).catch((err)=>{
            console.log(err);
        })
           
        

    }
    useEffect(()=>{
        fetchData();
    },[])
    const showData=()=>{
        fetch('http://127.0.0.1:8000/cart/', getCommonOptions()).then(response=>response.json())
        .then((data)=>{
            console.log(data);
            setShow(data);
        }).catch((err)=>{
            console.log(err);
        })
    }
    useEffect(()=>{
        showData();
    }, [])

    const showOrder=()=>{
        fetch('http://127.0.0.1:8000/order/order/', getCommonOptions()).then(response=>response.json())
        .then((data)=>{
            console.log(data);
            setOrder(data);
        }).catch((err)=>{
            console.log(err);
        })
    }
    useEffect(()=>{
        showOrder();
    }, [])

    return(
        <div className="checkoutContainer">
           <div id="checkoutRow1">
               <h1 className="checkoutHeading">Checkout</h1>
               
           </div>
           <div id="checkoutRow2">
               <div className="checkoutFullDetails">
                   <div className="headingCheckoutDetails">
                       <h3>Your added cart details are</h3>
                   </div>
                   <div className="checkoutDetailsShow">
                       {show.map((shows)=>
                           <div key={shows.id}>
                                <p>Your total unique items added are "{shows.item}" and total item quantity is "{shows.quantity}"</p>   
                            </div>
                       )}
                   </div>

               </div>
               <hr />
               <div className="branchClass">
                    <div id="headingBranch">
                        <h5>Select Branch for Order</h5>
                    </div>
                    <div id="selectBigBranch">
                        
                        <select>
                            
                            

                            
                            <option>Select Branch</option>
                            {branches.map((branch)=>
                            <option key={branch.id}>{branch.franchise_name}</option>
                            )}
                            
                        </select>
                    
                    </div>
               </div>
               <hr />
               <div className="paymentType">
                    <div id="headingPayment">
                        <h5>Select Payment Type</h5>
                    </div>
                    <div id="selectBigPayment">
                        
                        <select>
                            
                            <option>Select Branch</option>
                            {order.map((orders)=>
                            <option key={orders.id}>{orders.payment_type}</option>
                            )}
                            
                        </select>
                    
                    </div>
               </div>


           </div>

        </div>
    )
}
export default Checkout;
