import React, {useState} from "react";
import { CartProvider, useCart } from "react-use-cart";
import Home from "./home";
import startersData from './Data.js';


function Cart(){
    
    
    const {
        addItem,
        isEmpty,
        totalUniqueItems,
        items,
        updateItemQuantity,
        removeItem
    } = useCart();
    
    if(isEmpty) return <h1 className="emptyCart">Your Cart is Empty</h1>
    
    return(
        
        <section>
            
            <div className="cartRow1">
                {startersData.starter.map((p)=>
                    <div key={p.id}>
                        <img className="cartImage" src={p.img} alt="" />
                        <h3 className="cartName">{p.name}</h3>
                        <h3 className="cartArab">{p.arab}</h3>
                    </div>
                )}
            </div>
        </section>
    )
}
export default Cart;
