import React from 'react'
import { Navigate } from 'react-router-dom';
import {useAuth} from "../context/AuthProvider";
export const RequireAuth = ({children}) => {
    const auth=useAuth();
    if(!auth.user){
        return <Navigate to="/LoginAccount" />;
    }
    else{
        return children;
    }
}
