import React from "react";
import { useCart } from "react-use-cart";
import { useEffect, useState } from "react";
import startersData from "./Data";
import { loadStripe } from "@stripe/stripe-js";
import axios from 'axios';
import { Component } from "react";
import getCommonOptions from "./getCommonOptions";
import { useCallback } from "react";

import { v4 as uuid } from 'uuid';

function Cart(){
    let [branches, setBranches]=useState([]);
    
    const fetchData=()=>{
        
        fetch('http://127.0.0.1:8000/franchise/', getCommonOptions()).then(response=>response.json())
        .then((data)=>{
            const authID=data;
            setBranches(authID);

            
        }).catch((err)=>{
            console.log(err);
        })
           
        

    }
    useEffect(()=>{
        fetchData();
    },[])
   
   const changeCheckout=(e)=>{
       e.preventDefault();
       const {name, value}=e.target;
       setCheckoutData({...checkoutData, [name]:value});
       console.log(checkoutData);
   }
    
   const handleCheckout=(e)=>{
       e.preventDefault();
       const unique_id = uuid();
       const small_id = unique_id.slice(0,7);
       
       const main_data={total:checkoutData.total, internal_id:small_id, item_price:cartTotal, notes:checkoutData.notes,
       road:checkoutData.road, block:checkoutData.block, house:checkoutData.house,  }
       const authCheckout=JSON.parse(localStorage.getItem('authToken'));
        
       fetch('http://127.0.0.1:8000/cart/', {
        method:"POST",
        headers:{
            'Authorization': `token ${authCheckout}`,
            'Content-Type':'application/json',
            'Accept':'application/json'
        },
        body:JSON.stringify(main_data),
       } ).then((res)=>{
           console.log(res.data);       })

        
        
   } 
   
   
   
    
    
    
    
    
    


    
    const {
        isEmpty,
        totalUniqueItems,
        updateItemQuantity,
        items,
        removeItem,
        totalItems,
        cartTotal,
    } = useCart();
    const delivery=cartTotal+2.5;
    const VAT=parseFloat(delivery*0.05).toFixed(2);
    const totalAmount=parseFloat(delivery+VAT).toFixed(2);
    
    const checkoutValues={total:totalAmount, notes:"", block:"", road:"", house:""};
    let [checkoutData, setCheckoutData]=useState(checkoutValues);
   
   
  
    
    if(isEmpty) return <h1 className="emptyCart">Your Cart is Empty</h1>
    
    return(
        
        <section>
            
            <div className="mainSection">
            <h1 className="shoppingHeading">Shopping Cart</h1>
            <p className="paraShoppingHeading">You have {totalItems} in your shopping cart</p>   
               {items.map((item) =>
                    <div className="cartRow" key={item.id} >
                        <div>
                            <img className="cartImage" src={item.img} alt="" />
                        </div>
                        <div>
                            <h3 className="cartName">{item.name}</h3>
                            <h3 className="cartArab">{item.arab}</h3>
                        </div>
                        <div>
                            <ul>
                                <li>
                                    {item.quantity}*{item.name} &mdash;
                                    
                                    <button className="updateButton" onClick={()=>updateItemQuantity(item.id, item.quantity-1)}>
                                        -
                                    </button>
                                    <button className="updateButton" onClick={()=>updateItemQuantity(item.id, item.quantity+1)}>
                                        +
                                    </button>
                                    <button className="updateButton" onClick={()=>removeItem(item.id)}>
                                        &times;
                                    </button>
                                </li>  
                               
                            </ul>
                        </div>
                        
                    </div>
                )}
                <div className="cartRow2">
                    <div className="choose">
                        <div className="chooseHeading">
                            <h1>Choose</h1>
                        </div>
                        <div className="chooseBoxes">
                            <div className="deliveryBox">
                                <img src={require('./images/cart1.png')} height="180px" width="200px" alt="" />
                                <h3>Delivery</h3>
                            </div>
                            <div className="dineBox">
                                <img src={require('./images/cart2.png')} height="180px" width="200px" alt="" />
                                <h3>Dine In</h3> 
                            </div>
                            <div className="takeaway">
                                <img src={require('./images/cart3.png')} height="180px" width="200px" alt="" />
                                <h3>Takeaway</h3>
                            </div>
                        </div>
                        <div className="chooseData">
                            <div className="deliveryAddress">
                                <label>
                                    House/Building
                                    <br/>
                                        <input type="address" id="house" name="house" onChange={changeCheckout}/>
                                </label>
                                <br/>
                                <label>
                                    Block
                                    <br/>
                                <input type="address" id="block" name="block" onChange={changeCheckout}/>
                                </label>
                                <br/>
                                <label>
                                  Road
                                  <br/>  
                                <input type="address" id="road" name="road" onChange={changeCheckout}/>
                                </label>
                            </div>
                            <br/>
                            <div className="dineIn">
                                <h3 className="countdownHeading">00:00</h3>
                            </div>
                            

                            
                            <div   className="branchDrop">
                               
                                        
                                        
                                        <select name="branch_select" id="branch-select">
                                        
                                            <option>Select Branch</option>
                                            {branches.map((branch)=>
                                            
                                            <>
                                            <option key={branch.id}  name="branch_names">{branch.id}:{branch.franchise_name}</option>
                                            </>
                                            
                                            )}
                                             
                                               
                                            
                                            
                                            
                                           
                                           
                                        
                                        
                                        </select>
                                              
                                        
                            
                            </div>
                              

                        </div>

                    </div>
                    <hr/>
                    <div className="note">
                        <form>
                        <label>
                            Leave a note here
                            <input className="noteInput" type="text" id="noteID" onChange={changeCheckout} name="notes" value={checkoutData.notes}/>
                        </label> 
                        </form>
                    </div>
                    <hr/>
                    
                    <div className="cartRow3">
                        <h1 className="paymentHeading">Payment Summary</h1>
                        <div className="paymentSummary">
                            <p>Items Price Total: <mark>{cartTotal}BD</mark></p>
                            <p>Delivery Fee: <mark>{delivery}BD</mark> </p>
                            <p>VAT Fee: <mark>{VAT}BD</mark></p>
