function getCommonOptions(){
    const authToken=localStorage.getItem("token");

    if(!authToken){
        return  {};
    }
    return {
        method:"POST",
        headers:{
            'Authorization': 'token ${authToken}',
            'Accept':'application/json',
            "Content-Type":"application/json",
        },

    }
}

export default getCommonOptions;
