import React, {useState} from "react";
import { CartProvider, useCart } from "react-use-cart";
import Home from "./home";
import startersData from './Data.js';


function Cart(){
    
    
    const {
        isEmpty,
        totalUniqueItems,
        items,
        updateItemQuantity,
        removeItem
    } = useCart();
    
    if(isEmpty) return <h1 className="emptyCart">Your Cart is Empty</h1>
    
    return(
        
        <section>
            
            <div>
                {items.map((item)=>
                    <div className="cartRow" key={item.id} >
                        <div>
                            <img className="cartImage" src={item.img} alt="" />
                        </div>
                        <div>
                            <h3 className="cartName">{item.name}</h3>
                            <h3 className="cartArab">{item.arab}</h3>
                        </div>
                        <div>
                            <ul>
                                <li key={item.id}>
                                  {item.quantity} x {item.name} &mdash;
                                    <button 
                                    onClick={()=>updateItemQuantity(item.id, item.quantity-1)} 
                                    />
                                    -
                                    <span>{totalUniqueItems}</span>
                                    <button 
                                    onClick={()=>updateItemQuantity(item.id, item.quantity+1)}
                                    />
                                </li>
                            </ul>
                        </div>
                        
                    </div>
                
                )}
                
            </div>
        </section>
    )
}
export default Cart;
