import React from "react";
import OwlCarousel from 'react-owl-carousel';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';
import Home from "./home";

const options = {
    margin: 30,
    responsiveClass: true,
    nav: true,
    autoplay: false,
    navText: ["Prev", "Next"],
    smartSpeed: 1000,
    responsive: {
        0: {
            items: 1,
        },
        400: {
            items: 1,
        },
        600: {
            items: 2,
        },
        700: {
            items: 3,
        },
        1000: {
            items: 3,
        }
    },
};


const myItemCard=(props)=> {
    return (
        <OwlCarousel className="owl-carousel owl-theme" id="owl-settings" {...options}>
            <div className="item"> 
                <img src={props.img} alt="" />
                <h4 className="product1-title-english">{props.title}</h4>
                <h4 className="product1-title-arabic">{props.arab}</h4>
                <h4 className="product1-price">&nbsp;<mark>{props.price}</mark></h4>
                <button className="add-to-cart">Add to Cart</button>
            </div>
        </OwlCarousel>

    );
}
export default myItemCard;
