import React, {useState} from "react";
import { CartProvider, useCart } from "react-use-cart";

function Cart(){
    const {addItem}=useCart();

    const {
        isEmpty,
        totalUniqueItems,
        items,
        updateItemQuantity,
        removeItem
    } = useCart();
    
    if(isEmpty) return <h1 className="emptyCart">Your Cart is Empty</h1>
    
    return(
        <section>
            <div className="cartRow1">
                <h1 className="addCartHeading">Welcome to Cart Page{totalUniqueItems}</h1>
            </div>
        </section>
    )
}
export default Cart;
