import React, {Component} from "react";


export default class Login extends Component{
    state={
        credentials: {login_username:"", login_password:""}
    }
    inputChanged=(e)=>{
        const cred=this.state.credentials;
        cred[e.target.name]=e.target.values;
    }
    login=(e)=>{
        alert("logged in");
    }
    render(){
        return(
            <div>
                <div className="Container">
                    <div className="inner-box">
                        <div className="acc-row1">
                            <h1 className="accHeading">Enter Your Login Details Below...</h1>
                            <p className="accParaDetail">Enter your details below to login or continue as guest by clicking on the button below</p>
                        </div>
                        <div className="loginAccForm">
                            <form method="" action="">
                                <input  type="text" placeholder="Enter Your Username" name="login_username" value={this.state.credentials.login_username}
                                onChange={this.inputChanged}  ></input>
                                <input  type="password" placeholder="Enter Your Password" name="login_password" value={this.state.credentials.login_password}
                                onChange={this.inputChanged}  ></input>
                                <button onClick={this.login}  className="loginButton"  type="submit">Sign In</button>    
                            </form>

                       
                        </div>
                    </div>
                </div>
            </div>
        );
    
}
}
