import React, {useState, useEffect} from "react";
import {Link} from "react-router-dom";






function Account(){

    const initialValues={username:"", email:"", password:""};
    const [formValues, setFormValues]=useState(initialValues);
    const [formErrors, setFormErrors]=useState({});
    const [isSubmit, setIsSubmit]=useState(false);
    
    const handleChange=(e)=>{
        const {name, value}=e.target;
        setFormValues({...formValues, [name]:value});
        console.log(formValues);
    };
    
    const handleSubmit=(e)=>{
        e.preventDefault();
        setFormErrors(validate(formValues));
        setIsSubmit(true);
    };
    
    useEffect(() => {
        console.log(formErrors);
        if (Object.keys(formErrors).length === 0 && isSubmit) {
          console.log(formValues);
        }
      }, [formErrors, formValues, isSubmit]);
    
    const validate = (values) => { 
        const errors={};
        const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
        if(!values.username){
            errors.username="Username is required!";
        }
        if(!values.email){
            errors.email="Email is required!"; 
        } else if (!regex.test(values.email)) {
            errors.email = "This is not a valid email format!";
        }
        if(!values.password){
            errors.password="Password is required";
        } else if (values.password.length < 4) {
            errors.password = "Password must be more than 4 characters";
        } else if (values.password.length > 20) {
            errors.password = "Password cannot exceed more than 10 characters";
        }
        
        return errors;
    };
    
    
    return(
        
        

        <section>
            
            <div className="Container">
                <span className="linkClass"><Link to="/LoginAccount">Already Have An Account?</Link></span> 
                    <div className="inner-box">
                        <div className="acc-row1">
                            <h1 className="accHeading">Create An Account Here...</h1>
                            <p className="accParaDetail">Enter your details below to create an account for free</p>
                        </div>
                            
                        <div className="createAccForm">
                        {Object.keys(formErrors).length === 0 && isSubmit ? (
                            <div className="message">Signed in successfully</div>
                        ) : (
                            <pre>{JSON.stringify(undefined, 2)}</pre>
                        )}
                        
                        <form onSubmit={handleSubmit}  className="accFormClass">
                                <div className="field">
                                    <input type="text" id="username" name="username"  
                                    placeholder="Enter Your Username" required  value={formValues.username} onChange={handleChange}  />
                                </div>
                                
                                <p className="errorsClass">{formErrors.username}</p>
                                
                                <div className="field">
                                    <input type="password"  id="password"
                                    name="password"  placeholder="Enter Your Password"  required
                                    value={formValues.password} onChange={handleChange} />
                                </div>
                                
                                <p className="errorsClass">{formErrors.password}</p>
                                
                                <div className="field">
                                    <input type="email"  id="email"
                                    name="email"  placeholder="Enter Your Email"  required
                                    value={formValues.email} onChange={handleChange} />
                                </div>
                                
                                <p className="errorsClass">{formErrors.email}</p> 
                                
                                <div className="field">
                                    <input type="password" id="confirm_pwd"
                                    name="confirm"  placeholder="Retype Your Password Again" required 
                                    />
                                </div>
                                
                                <button className="accRegisterButton"   >Submit</button>
                        </form>
                            
                    </div>
                </div>
            </div> 
    </section>
            
        
            
);
    
}
export default Account;
