import React from 'react'
import { Navigate } from 'react-router-dom';
import {useAuth} from "../context/AuthProvider";
export const RequireAuth = ({children}) => {
    const auth=useAuth();
    if(!auth.user){
        return alert("Login First");
    }
    else{
        return children;
    }
}
