import React from "react";
import {useState, useEffect} from "react";
import getCommonOptions from "./getCommonOptions";
import { useCart } from "react-use-cart";

function Checkout(){
    const {totalItems}=useCart();
    const paymentValues={payment_type:""};  
    const [payment, setPayment]=useState(paymentValues);
    let [branches, setBranches]=useState([]);
    let [show, setShow]=useState([]);
    
    
    const fetchData=()=>{
           
        fetch('http://127.0.0.1:8000/franchise/', getCommonOptions()).then(response=>response.json())
        .then((data)=>{
            console.log(data);
            setBranches(data);
        }).catch((err)=>{
            console.log(err);
        })
           
        

    }
    useEffect(()=>{
        fetchData();
    },[])
    const showData=()=>{
        fetch('http://127.0.0.1:8000/cart/', getCommonOptions()).then(response=>response.json())
        .then((data)=>{
            console.log(data);
            setShow(data);
        }).catch((err)=>{
            console.log(err);
        })
    }
    useEffect(()=>{
        showData();
    }, [])

    const handleOrder=(e)=>{
        e.preventDefault();
        const data={payment_type:payment.main_value}
        const authPayment=JSON.parse(localStorage.getItem('authToken'));
        fetch('http://127.0.0.1:8000/order/order/', {
            method:"POST",
            headers:{
                'Authorization': `token ${authPayment}`,
                'Content-Type':'application/json',
                'Accept':'application/json'
            },
            body: JSON.stringify(data),  
        }).then((res)=>{
            console.log(res.data);
        })
    }
    const orderChange=(e)=>{
        const main_value=e.target.value;
       setPayment({...payment, main_value});
       console.log(payment);
    }
   

   

    
   

    return(
        <div className="checkoutContainer">
           <div id="checkoutRow1">
               <h1 className="checkoutHeading">Checkout</h1>
               
           </div>
           <div id="checkoutRow2">
               <div className="checkoutFullDetails">
                   <div className="headingCheckoutDetails">
                       <h3>Your added cart details are</h3>
                   </div>
                   <div className="checkoutDetailsShow">
                       
                           
                                <p>Your total item quantity is {totalItems}</p>   
                           
                       
                   </div>

               </div>
               <hr />
               <div className="branchClass">
                    <div id="headingBranch">
                        <h5>Select Branch for Order</h5>
                    </div>
                    <div id="selectBigBranch">
                        
                        <select>
                            
                            

                            
                            <option disabled selected={true}>Select Branch</option>
                            {branches.map((branch)=>
                            <option key={branch.id}>{branch.franchise_name}</option>
                            )}
                            
                        </select>
                    
                    </div>
               </div>
               <hr />
               <div className="paymentType">
                    <div id="headingPayment">
                        <h5>Select Payment Type</h5>
                    </div>
                    <div id="selectBigPayment">
                        
                        <select onChange={orderChange}>
                            
                            <option disabled selected={true}>Select Payment Type</option>
                            
                                
                                <option value={payment.main_value}>Cash</option>
                                <option value={payment.main_value}>Credit Card</option>
                                <option value={payment.main_value}>Debit Card</option>
                                
                                
                           
                            
                            
                        </select>
                    
                    </div>
               </div>
               <div className="placeOrderButton">
                   <button onClick={handleOrder}>Place Order</button>
               </div>


           </div>

        </div>
    )
}
export default Checkout;
